animateTouch.releaseAction:
Invoke Me.Operacao.Visible(False);
Invoke Me.AlmSumario.Visible(False);
Invoke Me.AlmHistorico.Visible(True);
Invoke Me.FactoryTalkAlarmandEventLogViewer1.Visible(True);
Invoke Me.FactoryTalkAlarmandEventLogViewer1.FilterName(#4);
Invoke Me.AbaOperacao.Visible(True);
Invoke Me.AbaAlmSumario.Visible(True);
Invoke Me.AbaAlmHistorico.Visible(False);

animateTouch.pressAction:
Invoke Me.Operacao.Visible(False);
Invoke Me.AlmSumario.Visible(True);
Invoke Me.AlmHistorico.Visible(False);
Invoke Me.AlarmEventSummary1.Visible(True);
Invoke Me.AlarmEventSummary1.FilterName(#4);
Invoke Me.AbaOperacao.Visible(True);
Invoke Me.AbaAlmSumario.Visible(False);
IF {Server\TSFTDSVR\geral_sts} THEN
Invoke Me.AbaAlmHistorico.Visible(True);
ENDIF;

Invoke Me.Operacao.Visible(True);
Invoke Me.AlmSumario.Visible(False);
Invoke Me.AlmHistorico.Visible(False);
Invoke Me.AbaOperacao.Visible(False);
Invoke Me.AbaAlmSumario.Visible(True);
IF {Server\TSFTDSVR\geral_sts} THEN
Invoke Me.AbaAlmHistorico.Visible(True);
ENDIF;

animateVisibility.expression:
(({#1.BCH} AND NOT {#1.YF})
OR {#1.CLOSING} 
OR ({#1.YF} AND {#1.XOL}) 
OR ({#1.YF} AND {#1.Y})) 
AND NOT {#1.BA}

(({#1.BCL} AND NOT {#1.YF})
OR {#1.OPENNING} 
OR ({#1.YF} AND {#1.XOH}) 
OR ({#1.YF} AND NOT {#1.Y})) 
AND NOT {#1.BA}
